import React, {useState} from "react";
import Axios from 'axios';

function LoginForm(){
  const [username, setUsername] = useState("")
  const [password, setPassword] = useState("")
  const [confirmPassword, setConfirmPassword] = useState("")

  const login = async () => {
    try {
      let result = await axios.post(          // any call like get
        "http://localhost:3001/login",         // your URL
        {                                     // data if post, put
          some: "data",
        }
      );
      console.log(result.response.data);
    } catch (error) {
      console.error(error.response.data);     // NOTE - use "error.response.data` (not "error")
    }
    let data = JSON.stringify({
      username: username,
      // password: password,
      // confirmPassword: confirmPassword
    });
    Axios.post('http://localhost:3000/login', data, {headers:{"Content-Type" : "application/json"}}).then(Response =>{
      console.log(Response)
    })
  }

  return(
    <>
    <div className="loginForm">
    <form>
      <h2>Login</h2>
      <label htmlFor="inputUsername">username:</label>
      <input typeof="text" name="inputUsername" id="inputUsername" type="TEXT" placeholder="Username:" onChange={e => { setUsername(e.target.value); console.log(username);} }></input>
      <label htmlFor="inputPassword">Password:</label>
      <input name="inputPassword" id="inputPassword" type="PASSWORD" placeholder="Password:" onChange={e => { setPassword(e.target.value); } }></input>
      <label htmlFor="inputConfirmPassword">Confirm Password:</label>
      <input name="inputConfirmPassword" id="inputConfirmPassword" type="PASSWORD" placeholder="Password:" onChange={e => { setConfirmPassword(e.target.value); } }></input>
      <a id="linkForgotPassword" href="...">Forgot Password</a>

    </form>
    <div id="loginBtnGroup">
      <button onClick={login} name="login" id="btnLogin">Login</button>
      <button id="btnSignUp">Sign Up</button>
    </div>
    </div>

      </>
  )
}

export default function Login(){
  return(
    <div className="loginFormContainer">
      <LoginForm></LoginForm>
    </div>

  )
}
