import React, {useState} from "react";
import Axios from 'axios';
import { LoginForm } from "./components/LoginForm";
import { LogRegBtnGroup } from "./components/LogRegBtnGroup";

function login({username, password, confirmPassword, setLoginError}) {
  if(username === ""){
    setLoginError("Please enter username")
  }
  if(password === ""){
    setLoginError("Please confirm password")
  }
  if(confirmPassword === ""){
    setLoginError("Please confirm password")
  }

  Axios.post("http://localhost:3000/login", {
    username: username,
    password: password,
    confirmPassword: confirmPassword
  },
  { withCredentials: true })
  .then(function (response) {
    console.log(response);
    if(response.data === 'successful'){
      document.location.href = "http://localhost:3001/home";
    }
  })
  .catch(function (error) {
    if (error.response) {
      console.log(error.response.data);
    }
  });
}


function LoginBox(){
  const [username, setUsername] = useState("")
  const [password, setPassword] = useState("")
  const [confirmPassword, setConfirmPassword] = useState("")
  const [loginError, setLoginError] = useState("")


  return(

    <div className="loginForm">
      <LoginForm loginError={loginError} setUsername={setUsername} setPassword={setPassword} setConfirmPassword={setConfirmPassword}></LoginForm>
      <LogRegBtnGroup login={login(username, password, confirmPassword, setLoginError)}></LogRegBtnGroup>
    </div>


  )
}

export default function Login(){
  return(
    <div className="loginFormContainer">
      <LoginBox></LoginBox>
    </div>

  )
}
